package com.patterns.printing;

public class Program10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
		1                
    	212
     	32123
  	    4321234
    	543212345

        1                
    	21 2
     	321 23
  	    4321 234
    	54321 2345
	
	*/	
	int i,j,k;
	
	
	for(i=1;i<=5;i++) {
		
		for(j=i;j>=1;j--) {
			System.out.print(j);
		}
       for(k=2;k<=i;k++) {
    	   System.out.print(k);
       }
	
	System.out.println();
	}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
