package com.patterns.printing;

public class Program2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	/*	1
	  	22
	  	333
	  	4444
	  	55555
*/
		
		
		int i,j,row=5;
		
		for(i=1;i<=row;i++) {
			for(j=1;j<=i;j++) {
				System.out.print(i);
			}
			System.out.println();
		}
		
		
		
		
		
		
		
	}

}
